import React from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

const Card = ({ card, onCardClick, onCardLike, onCardDelete, likes }) => {
  const currentUser = React.useContext(CurrentUserContext);

  const handleClick = (e) => {
    onCardClick(card);
  };

  const handleLikeClick = (e) => {
    onCardLike(card);
  };

  const isOwn = card.owner === currentUser._id;

  const cardDeleteButtonClassName = `elements__trash ${
    isOwn ? 'elements__trash_visible' : 'elements__trash_hidden'
  }`;

  const isLiked = card.likes.some((card) => {
    console.log(card.likes);
    let likeName;
    if (card === currentUser._id) {
      likeName = 'elements_image-heart elements__image-heart_active';
    } else {
      likeName = 'elements_image-heart';
    }
    return likeName;
  });

  // const isLiked = card.likes.some((i) => i._id === currentUser._id);
  // console.log(isLiked);
  // const classNameButton = 'elements__image-heart';
  // const active = 'active';
  // const likeClassName = `${
  //   isLiked === true ? `elements__image-heart_active` : classNameButton
  // } `;

  // console.log(isLiked, '[liking a card]');

  return (
    <li className="elements__item">
      <div
        className="elements__image"
        onClick={handleClick}
        style={{ backgroundImage: `url(${card.link})` }}
      />
      {isOwn && (
        <button
          className={cardDeleteButtonClassName}
          type="button"
          onClick={() => onCardDelete(card)}
        />
      )}
      <div className="elements__info">
        <h3 className="elements__title">{card.name}</h3>
        <button
          onClick={handleLikeClick}
          className={isLiked}
          type="button"
          name="like"
        />
        <h4 className="elements__like-count">{card.likes.length}</h4>
      </div>
    </li>
  );
};

export default Card;
